const { readFileSync } = require("fs");
const { createServer } = require("http");
const { parse } = require("url"); // Fix: Use "url" instead of "path"
const {renderToString} = require("react-dom/server")
const htmlTemplate = readFileSync(`${__dirname}/index.html`,'utf-8')
const React = require("react")
const pizzas = [
    {
      name: "Focaccia",
      price: 6,
    },
    {
      name: "Pizza Margherita",
      price: 10,
    },
    {
      name: "Pizza Spinaci",
      price: 12,
    },
    {
      name: "Pizza Funghi",
      price: 12,
    },
    {
      name: "Pizza Prosciutto",
      price: 15,
    },
  ];

  function Home() {
    return (
      <div>
        <h1>üçï Fast React Pizza Co.</h1>
        <p>This page has been rendered with React on the server ü§Ø</p>

        <h2>Menu</h2>
        <ul>
          {pizzas.map((pizza) => (
            <MenuItem pizza={pizza} key={pizza.name} />
          ))}
        </ul>
      </div>
    );
  }

  function Counter() {
    const [count, setCount] = React.useState(0);
    return (
      <div>
        <button onClick={() => setCount((c) => c + 1)}>+1</button>
        <span>{count}</span>
      </div>
    );
  }

  function MenuItem({ pizza }) {
    return (
      <li>
        <h4>
          {pizza.name} (${pizza.price})
        </h4>
        <Counter />
      </li>
    );
  }


const server = createServer((req, res) => {
  const pathName = parse(req.url, true).pathname; // Now correctly parses URL

  if (pathName === "/") {
    const renderedHtml = renderToString(<Home/>)
    res.writeHead(200,{"Content_Type":"text/html"})
    res.end(renderedHtml);
  } else if(pathName=== "/test") {
    res.end("Test");
  }
  else {
    res.end("Page Not Found");
  }
});

server.listen(8080, () => console.log("Listening for requests on port 8001")); // Fix: Correct log message
